var db = require("../models");

module.exports = {
  findAll: function findAll(req, res) {
    db.Item.find(req.query).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  findById: function findById(req, res) {
    db.Item.findById(req.params.id).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  update: function update(req, res) {
    db.Item.findOneAndUpdate({
      _id: req.params.id
    }, req.body).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  }
};
