var mongoose = require("mongoose");

var Schema = mongoose.Schema;
var areaSchema = new Schema({
  areas: [{
    name: {
      type: String,
      default: ""
    },
    isAccessible: {
      type: Boolean,
      default: false
    }
  }]
});
var Area = mongoose.model("Area", areaSchema);
module.exports = Area;
